public function pushToStack($stackName, $targetViews, $content)
{
    $targetViews = is_array($targetViews) ? $targetViews : [$targetViews];
    foreach ($targetViews as $targetView) {
        app()->make('events')->listen('composing: '.$targetView, function ($view) use ($stackName, $content) {
            $content = $content instanceof Closure ? $content($view) : $content;
            if (method_exists($view, 'getFactory') && method_exists($view->getFactory(), 'startPush')) {
                $view->getFactory()->startPush($stackName, $content);
            }
        });

public function call($name, $params = [])
{
    if (false === array_key_exists($name, $this->resolved)) {
        $handler = $this->get($name);
        if ($handler instanceof Closure) {
            $this->resolved[ $name ] = function ($value) use ($name, $handler) {
                return call_user_func_array($handler, [$value]);
            };
        } else {
            $class = $this->isCallableWithAtSign($handler) ? explode('@', $handler)[ 0 ] : $handler;
            $method = $this->isCallableWithAtSign($handler) ? explode('@', $handler)[ 1 ] : 'handle';

        $instance = $this->app->make($class);
        if ($instance instanceof DirectiveInterface === false) {
            throw InvalidDirectiveClassException::forClass($instance);
        }
        $instance->setName($name);
        $this->resolved[ $name ] = function ($value) use ($name, $instance, $method) {
            return call_user_func_array([$instance, $method], [$value]);
        };
    }
}

/**
 * Adds a Loop to the stack.
 */
public function start()
{
    $stackItem = static::create($args = func_get_args());
    array_push($this->stack, $stackItem);
    $stackItem->start();

    return $stackItem;
}

